/* Load in additional HTML fragments based on device category 'keyword', set by CSS3 MediaQuery viewport width
============================================================= */// A CSS3 MediaQuery sets the body:after CSS generated 'content' to an imaginary 
// device category 'keyword' like 'device-smartphone', 'device-tablet', 'device-desktop' or 'device-wide'
// based custom CSS 'breakpoints'. Javasript then reads that 'keyword' onLoad() (see above) and uses a 
// window.resize() eventhandler to load in additional HTML fragments (using Ajax) based on the URL in a 
// HTML5 data-attribute div, like so: <div data-device-desktop="/fragments/social-media.html"></div>
// the contents of the URL is appended to the div mathing the 'keyword' when CSS applies this keyword
// to the body:after generated 'content'
// Set vars and a list of device category names matching CSS's body:after CSS generated 'content:' list
function checkDeviceCategory(){try{deviceCategory=window.getComputedStyle(document.body,":after").getPropertyValue("content");deviceCategory=deviceCategory.replace('"',"","g");deviceCategory=deviceCategory.replace('"',"","g");var e=deviceCategorySequence.indexOf(deviceCategory)}catch(t){}switch(e){case 0:break;case 1:loadTabletFragments();break;case 2:loadTabletFragments();loadDesktopFragments();break;case 3:loadTabletFragments();loadDesktopFragments();loadWideFragments();break;default:loadTabletFragments();loadDesktopFragments();loadWideFragments()}}function loadTabletFragments(){deviceTabletLoaded||$("[data-device-tablet]").each(function(){$(this).load($(this).data("device-tablet"));deviceTabletLoaded=!0})}function loadDesktopFragments(){deviceDesktopLoaded||$("[data-device-desktop]").each(function(){$(this).load($(this).data("device-desktop"));deviceDesktopLoaded=!0})}function loadWideFragments(){deviceWideLoaded||$("[data-device-wide]").each(function(){$(this).load($(this).data("device-wide"));deviceWideLoaded=!0})}var deviceCategory,deviceCategorySequence=["device-smartphone","device-tablet","device-desktop","device-wide"],deviceTabletLoaded=!1,deviceDesktopLoaded=!1,deviceWideLoaded=!1;